import React, { useState, useEffect } from "react";

const App = () => {
  const [file, setFile] = useState(null);
  const [predictions, setPredictions] = useState([]);
  const [colors, setColors] = useState([]);
  const [error, setError] = useState(null);
  const [filePreview, setFilePreview] = useState(null);
  const [loading, setLoading] = useState(false);
  const [modelType, setModelType] = useState("mlp");
  const [apiHealth, setApiHealth] = useState(null);

  useEffect(() => {
    checkApiHealth();
  }, []);

  const checkApiHealth = async () => {
    try {
      const response = await fetch("http://localhost:8000/health");
      const data = await response.json();
      setApiHealth(data);
    } catch (error) {
      setApiHealth({ status: "unavailable", models_available: {} });
      setError("Cannot connect to the prediction service");
    }
  };

  const handleFileChange = (event) => {
    const selectedFile = event.target.files[0];
    if (selectedFile) {
      if (!selectedFile.type.startsWith("image/")) {
        setError("Please select an image file");
        setFile(null);
        setFilePreview(null);
        return;
      }

      setFile(selectedFile);
      const reader = new FileReader();
      reader.onloadend = () => setFilePreview(reader.result);
      reader.onerror = () => setError("Error reading file");
      reader.readAsDataURL(selectedFile);
    } else {
      setFile(null);
      setFilePreview(null);
    }
  };

  const handleModelChange = (event) => {
    setModelType(event.target.value);
    setPredictions([]);
    setColors([]);
    setError(null);
  };

  const handleSubmit = async (event) => {
    event.preventDefault();
    if (!file) {
      setError("Please select a file");
      return;
    }

    if (!apiHealth?.models_available[modelType]) {
      setError(`Selected model (${modelType}) is not available`);
      return;
    }

    const formData = new FormData();
    formData.append("file", file);
    setLoading(true);
    setError(null);

    try {
      const response = await fetch(
        `http://localhost:8000/predict?model_type=${modelType}`,
        {
          method: "POST",
          body: formData,
        }
      );

      if (!response.ok) {
        const errorData = await response.json();
        throw new Error(errorData.detail || "Prediction failed");
      }

      const data = await response.json();
      setPredictions(data.predicted_emotions);
      setColors(data.colors);
    } catch (error) {
      setError(`Error: ${error.message}`);
      setPredictions([]);
      setColors([]);
    } finally {
      setLoading(false);
    }
  };

  const handleReset = () => {
    setFile(null);
    setPredictions([]);
    setColors([]);
    setError(null);
    setFilePreview(null);
  };

  return (
    <div className="container mx-auto px-4 mt-8 max-w-4xl">
      <h1 className="text-3xl font-bold text-center mb-8">
        Image Emotion Predictor
      </h1>

      {apiHealth && apiHealth.status !== "healthy" && (
        <div className="bg-yellow-100 border-l-4 border-yellow-500 p-4 mb-4">
          Warning: Prediction service is not available
        </div>
      )}

      <form onSubmit={handleSubmit} className="space-y-6 text-center">
        <div>
          <input
            type="file"
            className="block w-full text-sm text-gray-500 file:mr-4 file:py-2 file:px-4 file:rounded-md file:border-0 file:text-sm file:font-semibold file:bg-blue-50 file:text-blue-700 hover:file:bg-blue-100"
            onChange={handleFileChange}
            accept="image/*"
          />
        </div>

        <div>
          <select
            className="w-full max-w-xs px-4 py-2 rounded-md border border-gray-300"
            value={modelType}
            onChange={handleModelChange}
            disabled={!apiHealth?.status === "healthy"}
          >
            <option value="mlp" disabled={!apiHealth?.models_available?.mlp}>
              MLP Model {!apiHealth?.models_available?.mlp && "(Unavailable)"}
            </option>
            <option value="svm" disabled={!apiHealth?.models_available?.svm}>
              SVM Model {!apiHealth?.models_available?.svm && "(Unavailable)"}
            </option>
            <option
              value="random_forest"
              disabled={!apiHealth?.models_available?.random_forest}
            >
              Random Forest Model{" "}
              {!apiHealth?.models_available?.random_forest && "(Unavailable)"}
            </option>
          </select>
        </div>

        <div className="space-x-4">
          <button
            type="submit"
            className={`px-4 py-2 rounded-md ${
              loading || !file || !apiHealth?.status === "healthy"
                ? "bg-blue-300 cursor-not-allowed"
                : "bg-blue-500 hover:bg-blue-600"
            } text-white font-medium`}
            disabled={loading || !file || !apiHealth?.status === "healthy"}
          >
            {loading ? "Processing..." : "Predict Emotion"}
          </button>
          <button
            type="button"
            className="px-4 py-2 rounded-md bg-gray-500 hover:bg-gray-600 text-white font-medium disabled:bg-gray-300 disabled:cursor-not-allowed"
            onClick={handleReset}
            disabled={loading}
          >
            Reset
          </button>
        </div>
      </form>

      {filePreview && (
        <div className="mt-8 text-center">
          <h5 className="text-lg font-medium mb-2">Image Preview:</h5>
          <img
            src={filePreview}
            alt="Preview"
            className="max-w-sm mx-auto rounded-lg"
          />
        </div>
      )}

      {error && (
        <div className="mt-4 p-4 bg-red-100 border-l-4 border-red-500 text-red-700">
          {error}
        </div>
      )}

      {predictions.length > 0 && (
        <div className="mt-8 text-center">
          <h2 className="text-2xl font-bold mb-4">Predicted Emotions</h2>
          <ul className="space-y-2 mb-8">
            {predictions.map((item, index) => (
              <li
                key={index}
                className="p-2 bg-gray-50 rounded-md inline-block min-w-[200px]"
              >
                {item.emotion} ({(item.probability * 100).toFixed(2)}%)
              </li>
            ))}
          </ul>

          <h3 className="text-xl font-bold mb-4">Primary Colors</h3>
          <div className="flex flex-wrap justify-center gap-4">
            {colors.map((color, index) => (
              <div key={index} className="text-center">
                <div
                  className="w-16 h-16 rounded border border-gray-200"
                  style={{
                    backgroundColor: `rgb(${color.rgb[0]}, ${color.rgb[1]}, ${color.rgb[2]})`,
                  }}
                />
                <p className="mt-2 text-sm">
                  <span className="font-bold">RGB:</span> (
                  {color.rgb.join(", ")})
                  <br />
                  <span className="font-bold">{color.name}</span>
                </p>
              </div>
            ))}
          </div>
        </div>
      )}
    </div>
  );
};

export default App;
