import React, { useState } from "react";
import axios from "axios";
import "./App.css";
import { Spinner } from "react-bootstrap";

function App() {
  const [file, setFile] = useState(null);
  const [predictions, setPredictions] = useState([]);
  const [colors, setColors] = useState([]);
  const [error, setError] = useState(null);
  const [filePreview, setFilePreview] = useState(null);
  const [loading, setLoading] = useState(false);
  const [modelType, setModelType] = useState("mlp");

  const handleFileChange = (event) => {
    const selectedFile = event.target.files[0];
    setFile(selectedFile);

    if (selectedFile) {
      if (!selectedFile.type.startsWith("image/")) {
        setError("Please select an image file");
        setFile(null);
        setFilePreview(null);
        return;
      }

      const reader = new FileReader();
      reader.onloadend = () => {
        setFilePreview(reader.result);
      };
      reader.readAsDataURL(selectedFile);
    } else {
      setFilePreview(null);
    }
  };

  const handleModelChange = (event) => {
    setModelType(event.target.value);
    setPredictions([]);
    setColors([]);
    setError(null);
  };

  const getErrorMessage = (error) => {
    if (error.response?.data?.detail) {
      return error.response.data.detail;
    } else if (error.response?.data) {
      return typeof error.response.data === "string"
        ? error.response.data
        : JSON.stringify(error.response.data);
    } else if (error.message) {
      return error.message;
    } else {
      return "An unexpected error occurred";
    }
  };

  const handleSubmit = async (event) => {
    event.preventDefault();
    if (!file) {
      setError("Please select a file");
      return;
    }

    const formData = new FormData();
    formData.append("file", file);

    setLoading(true);
    setError(null);
    setPredictions([]);
    setColors([]);

    try {
      // Health check
      try {
        const healthCheck = await axios.get("http://localhost:8000/health");
        if (!healthCheck.data.models_available[modelType]) {
          throw new Error(`${modelType.toUpperCase()} model is not available`);
        }
      } catch (healthError) {
        throw new Error(
          "Server is not responding. Please ensure the backend service is running."
        );
      }

      // Prediction request
      const response = await axios.post(
        `http://localhost:8000/predict?model_type=${modelType}`,
        formData,
        {
          headers: {
            "Content-Type": "multipart/form-data",
          },
          timeout: 30000,
        }
      );

      // Validate response data
      if (!response.data) {
        throw new Error("No data received from server");
      }

      if (!response.data.predicted_emotions || !response.data.colors) {
        throw new Error("Invalid response format from server");
      }

      setPredictions(response.data.predicted_emotions);
      setColors(response.data.colors);
    } catch (error) {
      console.error("Error in handleSubmit:", error);
      setError(getErrorMessage(error));
      setPredictions([]);
      setColors([]);
    } finally {
      setLoading(false);
    }
  };

  const handleReset = () => {
    setFile(null);
    setPredictions([]);
    setColors([]);
    setError(null);
    setFilePreview(null);
  };

  return (
    <div className="App container mt-5">
      <h1 className="text-center mb-4">Image Emotion Predictor</h1>

      <form onSubmit={handleSubmit} className="text-center">
        <div className="mb-3">
          <input
            type="file"
            className="form-control"
            onChange={handleFileChange}
            accept="image/*"
          />
        </div>
        <div className="mb-3">
          <select
            className="form-select"
            value={modelType}
            onChange={handleModelChange}
          >
            <option value="mlp">MLP Model</option>
            <option value="svm">SVM Model</option>
            <option value="random_forest">Random Forest Model</option>
          </select>
        </div>
        <div>
          <button
            type="submit"
            className="btn btn-primary me-2"
            disabled={loading || !file}
          >
            {loading ? "Predicting..." : "Predict Emotion"}
          </button>
          <button
            type="button"
            className="btn btn-secondary"
            onClick={handleReset}
            disabled={loading}
          >
            Reset
          </button>
        </div>
      </form>

      {filePreview && (
        <div className="image-preview mt-4 text-center">
          <h5>Image Preview:</h5>
          <img
            src={filePreview}
            alt="Preview"
            className="img-fluid preview-image"
            style={{ maxWidth: "300px", height: "auto" }}
          />
        </div>
      )}

      {loading && (
        <div className="text-center mt-3">
          <Spinner animation="border" role="status">
            <span className="visually-hidden">Loading...</span>
          </Spinner>
          <p className="mt-2">
            Processing with {modelType.toUpperCase()} model...
          </p>
        </div>
      )}

      {error && (
        <div className="alert alert-danger mt-3 text-center">
          <strong>Error:</strong> {error}
        </div>
      )}

      {predictions.length > 0 && (
        <div className="results mt-4 text-center">
          <h2>Predicted Emotions</h2>
          <ul className="list-group mb-3 text-center">
            {predictions.map((item, index) => (
              <li key={index} className="list-group-item">
                {item.emotion} ({(item.probability * 100).toFixed(2)}%)
              </li>
            ))}
          </ul>
          <h3>Primary Colors</h3>
          <div className="colors d-flex justify-content-center flex-wrap mt-3">
            {colors.map((color, index) => (
              <div key={index} className="color-card text-center mx-2 my-3">
                <div
                  className="color-box"
                  style={{
                    backgroundColor: `rgb(${color.rgb[0]}, ${color.rgb[1]}, ${color.rgb[2]})`,
                  }}
                ></div>
                <div className="color-info mt-2">
                  <strong>RGB:</strong> ({color.rgb.join(", ")})<br />
                  <strong>{color.name}</strong>
                </div>
              </div>
            ))}
          </div>
        </div>
      )}
    </div>
  );
}

export default App;
